template:
{% if  onu.interface._technology == 'gpon' %}
conf t
interface {{onu.interface.iface_name}}
onu {{free_onu_numbers.first}} type {{params.type_ont}} sn {{onu.serial}}
ex 
interface {{onu.interface._technology}}-onu_{{onu.interface._shelf}}/{{onu.interface._slot}}/{{onu.interface._port}}:{{free_onu_numbers.first}}
  name {{params.device_name_custom}}
  description {{params.device_name_custom}}
  tcont 1 name {{params.profile_speed}} profile {{params.profile_speed}}
  gemport 1 name {{params.profile_speed}} tcont 1
  gemport 1 traffic-limit downstream {{params.profile_speed}}
  switchport mode trunk vport 1
  service-port 1 vport 1 user-vlan {{params.vlan}} vlan {{params.vlan}}
  service-port 1 description HSI
  security storm-control broadcast rate 20 direction egress vport 1
  security storm-control broadcast rate 20 direction ingress vport 1
  ip dhcp snooping enable vport 1
  ip-source-guard enable sport 1
exit
pon-onu-mng {{onu.interface._technology}}-onu_{{onu.interface._shelf}}/{{onu.interface._slot}}/{{onu.interface._port}}:{{free_onu_numbers.first}}
  loop-detect ethuni eth_0/1 enable
  loop-detect ethuni eth_0/2 enable
  loop-detect ethuni eth_0/3 enable
  loop-detect ethuni eth_0/4 enable
  flow mode 1 tag-filter vlan-filter untag-filter discard
  flow 1 pri 0 vlan {{params.vlan}}
  gemport 1 flow 1
  switchport-bind switch_0/1 iphost 1
  ip-host 1 dhcp-enable enable ping-response enable traceroute-response enable
  vlan-filter-mode iphost 1 tag-filter vlan-filter untag-filter discard
  vlan-filter iphost 1 pri 0 vlan {{params.vlan}}
  dhcp-ip ethuni eth_0/1 from-onu
  dhcp-ip ethuni eth_0/2 from-onu
  dhcp-ip ethuni eth_0/3 from-onu
  dhcp-ip ethuni eth_0/4 from-onu
  tr069-mgmt 1 acs http://tr069.your.server:7547
end
wr
{% endif %}
